import http from './httpService';
import { Redirect } from 'react-router';


export function getAllProducts() {
    return http.get(`http://localhost:3900/api/admin/`);
}

export function addProducts(data) {
    return http.post(`http://localhost:3900/api/admin/`, data);
}

export function editProduct(productID) {
    return http.put(`http://localhost:3900/api/admin/edit/${productID}`)
}

export function deleteProduct(productID) {
    return http.delete(`http://localhost:3900/api/admin/delete/${productID}`)
    Redirect('/')
}

const ex = {
    getAllProducts,
    addProducts,
    deleteProduct

}

export default ex;
