import React from 'react';
import homeService from '../services/homeService';

const HomeProduct = ({ product }) => {
    return (
        <React.Fragment>
            <div className="card col-4 m-2">
                <img className="card-img-top" width="100%" height="100%" src={product.imageUrl} alt="Card cap" />
                <div className="card-body">
                    <h5 className="card-title">{product.title}</h5>
                    <p className="card-text">{product.description}</p>
                    <p>  {product.price}$</p>
                    <button onClick={() => { homeService.getProduct(product._id).then(console.log(localStorage.getItem('productID'))) }} className="btn btn-primary">buy Now</button>
                </div>
            </div>

        </React.Fragment >
    );
}

export default HomeProduct;
