import { render } from '@testing-library/react';
import React, { Component } from 'react';
import homeService from '../services/homeService';

class HomeProduct extends Component {

    state = {
        cartProducts: []
    }



    async getProduct() {
        let { product } = this.props;
        const { data } = await homeService.getProduct(product._id);
        this.setState({ cartProducts: 0 })
    }


    render() {



        return (
            <React.Fragment>
                <div className="card col-4 m-2">
                    <img className="card-img-top" width="100%" height="100%" src={this.product.imageUrl} alt="Card cap" />
                    <div className="card-body">
                        <h5 className="card-title">{this.product.title}</h5>
                        <p className="card-text">{this.product.description}</p>
                        <p>  {product.price}$</p>
                        <button onClick={() => { this.getProduct() }} className="btn btn-primary">buy Now</button>
                    </div>
                </div>

            </React.Fragment >
        );

    }


}

export default HomeProduct;
