import React, { Component } from 'react';
import Product from './product';
import { Link } from 'react-router-dom';
import productService from '../services/productService';
class Admin extends Component {
    state = {
        products: []
    }

    async componentDidMount() {

        const { data } = await productService.getAllProducts();
        console.log(data)
        if (data.length > 0) this.setState({ products: data });
    }

    render() {
        return (
            <React.Fragment>
                <div className="container mt-5">
                    <Link to="/add-item">  <button className="btn btn-primary" >Add item</button></Link>

                    <table class="table table-striped mt-5 ">
                        <thead>
                            <tr>
                                <th scope="col">Title</th>
                                <th scope="col">Price</th>
                                <th scope="col">Descriptions</th>
                                <th scope="col">Image</th>
                                <th scope="col">Options</th>
                            </tr>
                        </thead>

                        <tbody>
                            <tr>
                                <th scope="row">{product.title}</th>
                                <td>{product.price}$</td>
                                <td>
                                    <p>{product.description}</p>
                                </td>
                                <td>
                                    <img src={product.imageUrl} width="50px" height="50px" alt="" />
                                </td>
                                <td>
                                    <Link to="edit-col"> <button className="btn btn-primary">Edit</button></Link>
                                    <Link to="delete-col"><button className="btn btn-danger">Delete</button></Link>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </React.Fragment >
        );
    }
}

export default Admin;
