import http from './httpService';

export function removeFromCart() {
    return http.put(`http://localhost:3900/api/admin/cart`);
}

export function getAllProducts() {
    return http.get(`http://localhost:3900/api/admin/`);
}

export function addProducts(data) {
    return http.post(`http://localhost:3900/api/admin/`, data);
}

export function deleteProduct(productID) {
    window.location = '/admin'
    return http.delete(`http://localhost:3900/api/admin/delete/${productID}`)
}

export function getProduct(productID) {//get product i
    localStorage.setItem('productID', productID);
    return http.get(`http://localhost:3900/api/admin/${productID}`)
}

export function editProduct(product) {//edit the product
    const productID = product._id;
    delete product._id;
    return http.put(`http://localhost:3900/api/admin/${productID}`, product)
}





const ex = {
    getAllProducts,
    addProducts,
    deleteProduct,
    getProduct,
    editProduct,
    removeFromCart
}

export default ex;
