import http from './httpService';

export function getCartProducts() {
    return http.get(`http://localhost:3900/api/home/cart`)
}

export function addToCart(productID) {
    window.location = '/home'
    return http.put(`http://localhost:3900/api/home/cart/${productID}`)
}

export function removeFromCart() {//remove from cart
    return http.put(`http://localhost:3900/api/home/cart`);
}


const ex = {
    getCartProducts,
    addToCart,
    removeFromCart

}

export default ex;
