import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import productService from '../services/productService';
import Joi from "joi-browser";
import Form from '../common/form';


class AddProducts extends Form {
    state = {
        products: {
            title: "", price: "", description: "", imageUrl: ""
        }
    }

    schema = {
        title: Joi.string().min(2).max(255).required(),
        price: Joi.string().min(1).max(10).required(),
        description: Joi.string().min(2).max(1024).required(),
        imageUrl: Joi.string().min(2).max(2024).required()
    };

    

    render() {
        return (
            <React.Fragment>
                <div className="container mt-5">

                    <table class="table table-striped mt-5 ">
                        <thead>
                            <tr>
                                <th scope="col">Title</th>
                                <th scope="col">Price</th>
                                <th scope="col">Descriptions</th>
                                <th scope="col">Image</th>
                                <th scope="col">Options</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <form on onSubmit={this.handleSubmit} method="POST">
                                    {this.renderInput('title', 'Title', 'title')}
                                    {this.renderInput('price', 'Price', 'price')}
                                    {this.renderInput('description', 'Description', 'description')}
                                    {this.renderInput('imageUrl', 'Image url', 'ImageUrl')}

                                </form>

                                <th scope="row">title here</th>
                                <td>price here</td>
                                <td>
                                    <p>description here</p>
                                </td>
                                <td>
                                    <img src="" width="50px" height="50px" alt="" />
                                </td>
                                <td>
                                    <Link to="edit-col"> <button className="btn btn-primary">Upload</button></Link>
                                    <Link to="edit-col"> <button className="btn btn-danger">Cancel</button></Link>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </React.Fragment >
        );
    }
}

export default AddProducts;
