import http from './httpService';



export function getProduct(productID) {//get product
    localStorage.setItem('productID', productID);
    return http.get(`http://localhost:3900/api/home/${productID}`)
}

export function getAllProducts() {//get product
    return http.get(`http://localhost:3900/api/home`)
}



const ex = {
    getCartProducts,
    addToCart,
    removeFromCart,
    getProduct,
    getAllProducts

}

export default ex;
