import React, { Component } from 'react';
import statsService from '../services/statsService';


class Stats extends React.Component {

    state = {
        topSellers: [],
        uniqueSellers: [],

    }

    async componentDidMount() {
        let { data } = await statsService.getTopSellers()
        this.setState({ topSellers: data });

        data = await statsService.getUniqueSellers()
        this.setState({ uniqueSellers: data });
        console.log(this.state.uniqueSellers.data);
    }

    render() {
        let { topSellers, uniqueSellers } = this.state

        return (
            <div className="container shadow p-3 mb-5 bg-white rounded d-flex justify-content-center">
                <div className="col-3 row m-4">
                    <div className="card mt-5">
                        <div className="card-title text-center">
                            <h4>top sellers</h4>
                        </div>
                        <hr />
                        {topSellers.map(item => {
                            return (
                                <div className="card-title">
                                    <h5>{item.title}</h5>
                                </div>)
                        })}

                    </div>
                </div>

                <div className="col-3 row m-4">
                    <div className="card">
                        <div className="card-title text-center ">
                            <h4>top unique</h4>
                        </div>
                        <hr />

                        {topUniqueSellers.map(item => {
                            return (
                                <div className="card-title">
                                    <h5>{item.title}</h5>
                                </div>)
                        })}



                    </div>
                </div>
                <div className="col-3 row m-4">
                    <div className="card mt-5">
                        <div className="card-title text-center">
                            <h4>top in past 5 days</h4>
                        </div>
                        <hr />
                        <div className="card-title m-2">
                            <h5>1) top sellers</h5>
                        </div>
                        <div className="card-title m-2">
                            <h5>2) top sellers</h5>
                        </div>
                        <div className="card-title m-2">
                            <h5>3) top sellers</h5>
                        </div>
                        <div className="card-title m-2">
                            <h5>4) top sellers</h5>
                        </div>
                        <div className="card-title m-2">
                            <h5>5) top sellers</h5>
                        </div>
                    </div>
                </div>


            </div>
        );
    }
}

export default Stats;
