import React, { Component } from 'react';

class Stats extends Component {
    state = {}
    render() {
        return (

            <React.Fragment>
                <div className="container shadow p-3 mb-5 bg-white rounded">
                    <div className="dropdown show">
                        <a className="btn btn-secondary dropdown-toggle" href="#" role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">cart <span>{localStorage.getItem('items')}</span> </a>

                        <div className="dropdown-menu" aria-labelledby="dropdownMenuLink">
                            <div className="row m-2 ">
                                <Cart />
                                <p>total price: <b>{localStorage.getItem('total')}$</b> </p>
                                <Link to="checkout" >  <button className="btn btn-primary">pay now</button></Link>
                            </div>
                        </div>
                    </div>
                    <div className="row p-5 ">
                        
                    </div>
                </div>
            </React.Fragment >



        );
    }
}

export default Stats;
