import React, { Component } from 'react';

import homeService from '../services/homeService';
import Home from './home';

class HomeProduct extends Component {

    state = {
        cart: []

    }

    async setProductDetails() {
        const { data } = await homeService.getProduct(localStorage.getItem('productID'));
        this.setState({ cart: data })
    }

    render() {
        const { product } = this.props;

        return (
            <div className="card col-4 m-2">
                <img className="card-img-top" width="100%" height="100%" src={product.imageUrl} alt="Card cap" />
                <div className="card-body">
                    <h5 className="card-title">{product.title}</h5>
                    <p className="card-text">{product.description}</p>
                    <p>  {product.price}$</p>
                    <button onClick={() => { homeService.getProduct(product._id).then(this.setProductDetails) }} className="btn btn-primary">buy Now</button>
                </div>
            </div>
        )
    }
}

export default HomeProduct;
