import React, { Component } from 'react';
import productService from '../services/productService';
import homeService from '../services/homeService'
import HomeProduct from './home-product';
import Cart from './cart';
import { Link } from 'react-router-dom';


class Home extends Component {
    state = {
        products: [],
        total: ''
    }



    async componentDidUpdate() {
        const { data } = await productService.getAllProducts();
        let total = localStorage.getItem('items')
        this.setState({ total: total })
        if (data.length > 0) this.setState({ products: data, total: total });
        console.log(this.state.total);
    }



    render() {
        const { products } = this.state;

        return (
            <React.Fragment>
                <div className="container shadow p-3 mb-5 bg-white rounded">
                    <div className="dropdown show">
                        <a className="btn btn-secondary dropdown-toggle" href="#" role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">cart <span>{this.state.total}</span> </a>

                        <div className="dropdown-menu" aria-labelledby="dropdownMenuLink">
                            <div className="row m-2 ">
                                <Cart />
                                <p>total price: <b>{localStorage.getItem('total')}$</b> </p>
                                <Link to="checkout" >  <button onClick={() => { homeService.removeFromCart() }} className="btn btn-primary">pay now</button></Link>
                            </div>
                        </div>
                    </div>
                    <div className="row p-5 ">
                        {products.map(product => <HomeProduct product={product} key={product._id} />)}
                    </div>
                </div>
            </React.Fragment >
        );
    }
}

export default Home;
