import { render } from '@testing-library/react';
import React, { Component } from 'react';
import homeService from '../services/homeService';

class HomeProduct extends Component {

    state = {
        products: [],
        cartProducts: []
    }

    async componentDidMount() {
        let { product } = this.props;
        this.setState({ product: product })
    }




    render() {

        async function getProduct() {
            const { data } = await homeService.getProduct(this.product._id);
            this.setState({ cartProducts: 0 })
        }


        return (
            <React.Fragment>
                <div className="card col-4 m-2">
                    <img className="card-img-top" width="100%" height="100%" src={product.imageUrl} alt="Card cap" />
                    <div className="card-body">
                        <h5 className="card-title">{product.title}</h5>
                        <p className="card-text">{product.description}</p>
                        <p>  {product.price}$</p>
                        <button onClick={() => { this.getProduct() }} className="btn btn-primary">buy Now</button>
                    </div>
                </div>

            </React.Fragment >
        );

    }


}

export default HomeProduct;
