import React, { Component } from 'react';
import productService from '../services/productService';
import homeService from '../services/homeService'
import HomeProduct from './home-product';
import Cart from './cart';
import { Link } from 'react-router-dom';


class Home extends Component {
    state = {
        products: [],
        cartProducts: []
    }

    async componentDidMount() {
        const { data } = await productService.getAllProducts();
        if (data.length > 0) this.setState({ products: data });
    }

    componentDidUpdate() {
        
    }


    render() {
        const { products } = this.state;

        return (
            <React.Fragment>
                <div className="container shadow p-3 mb-5 bg-white rounded">
                    <div className="dropdown show">
                        <a className="btn btn-secondary dropdown-toggle" href="#" role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">cart <span>number of products</span> </a>
                        <div className="dropdown-menu" aria-labelledby="dropdownMenuLink">
                            <div className="row m-2 ">


                            </div>
                        </div>
                    </div>

                    <div className="row p-5 ">
                        {products.map(product => <HomeProduct product={product} key={product._id} />)}
                    </div>
                </div>
            </React.Fragment >
        );
    }
}

export default Home;
