import http from './httpService';

export function getCartProducts() {
    return http.get(`http://localhost:3900/api/home/cart`)
}

export function addToCart(productID) {
    return http.put(`http://localhost:3900/api/home/cart/${productID}`)
}

export function removeFromCart() {//remove from cart
    return http.put(`http://localhost:3900/api/home/cart`);
}

export function getProduct(productID) {//get product
    localStorage.setItem('productID', productID);
    return http.get(`http://localhost:3900/api/home/${productID}`)
}

export function getAllProduct() {//get product
    return http.get(`http://localhost:3900/api/home`)
}



const ex = {
    getCartProducts,
    addToCart,
    removeFromCart,
    getProduct

}

export default ex;
