import React, { Component } from 'react';
import productService from '../services/productService';
import homeService from '../'

class Cart extends Component {
    state = {
        products: [],
        topSellers: []
    }

    async componentDidMount() {
        const { product } = this.props;
        const { data } = await productService.getCartProducts();
        if (data.length > 0) this.setState({ products: data });
        localStorage.setItem('items', this.state.products.length)

        let sum = 0
        let total = []
        for (let x = 0; x < this.state.products.length; x++) {
            total.push(this.state.products[x].price);
            sum += total[x]
        }
        this.setState({ total: sum })
        localStorage.setItem('total', sum)
    }



    render() {
        const { products } = this.state;

        return (
            <div className="col p-2 ">
                {products.map(product => (
                    <div>
                        <p>{product.title} | <b> {product.price}$ </b></p>
                        <hr />
                    </div>
                ))}
            </div>
        );
    }
}

export default Cart;
