import React, { Component } from 'react';
import homeService from '../services/homeService';
import HomeProduct from './home-product';
import { CartProvider } from 'react-use-cart'

import Cart from './cart';



class Home extends Component {
    state = {
        products: [],
        cartProducts: [],
    }

    async componentDidMount() {
        const { data } = await homeService.getAllProducts();
        if (data.length > 0) this.setState({ products: data });
    }

    render() {
        const { products } = this.state;

        console.log(localStorage.getItem('productID'));



        return (
            <CartProvider>
                <div className="container shadow p-3 mb-5 bg-white rounded">
                    <div className="dropdown show">
                        <a className="btn btn-secondary dropdown-toggle" role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">cart <span></span> </a>
                        <div className="dropdown-menu" aria-labelledby="dropdownMenuLink">
                            <div className="row m-2 ">
                                <Cart />
                                <hr />
                                <p>total price: </p>
                                <button className="btn btn-pri mary">pay now</button>
                            </div>
                        </div>
                    </div>
                    <div className="row p-5 ">
                        {products.map((item, index) => {
                            return (
                                <HomeProduct
                                    id={item._id}
                                    img={item.imageUrl}
                                    title={item.title}
                                    desc={item.description}
                                    price={item.price}
                                    item={item}
                                    key={index}
                                />)
                        }
                        )
                        }

                    </div>
                </div>
            </CartProvider >
        );
    }
}

export default Home;
